package gitpackage;


public class Test1 {

	int num=3;
	public void even()
	{
		if(num%2==0)
		{
			System.out.println("is even number");
			
		}
		else
		{
			System.out.println("is odd number");
		}
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Test1 primeNumber=new Test1();
primeNumber.even();
	}


}
